sysauto_script.script:
var rightNow=new GlideDateTime();
var tomor=gs.hoursAgo(-24);
//var tomor=rightNow.addDays(-1);
gs.info('tomor >> '+tomor+' >> rightNow '+rightNow);
var gr=new GlideRecord('x_58872_needit_needit_task');
gr.addQuery('due_date','>=',rightNow);
gr.addQuery('due_date','<',tomor);
gr.addQuery('state','!=',3);
gr.query();
gs.info('Im in '+gr.getRowCount());
while(gr.next()){
	//gs.addInfoMessage('Im in '+gr.getRowCount());
	gs.info('Theese NeedIt tasks are due soon : '+gr.number);
	gs.eventQueue('x_58872_needit.needTaskDewin24',gr,gr.number,gs.getUserName());
}
