sysauto_script.script:
var howOverdue=gs.getProperty('x_58872_needit.autoCloseOverdue');
gs.info('howOverdue '+howOverdue);
var gr=new GlideRecord('x_58872_needit_needit_task');
gr.addQuery('due_date','<=',gs.daysAgo(howOverdue));
gr.addQuery('state','<',3);
gr.query();
gs.info('gr count '+gr.getRowCount());
while(gr.next()){
		gs.info("Auto close NeedIt Task record = " + gr.number);
	gr.work_notes='Record closed because it was too far past the Due date.';
	gr.state=4;
	gr.update();
}
